<style lang="scss" scoped>
svg .arara,
svg .caneta {
  scale: 1;
  transform-origin: center;
}
svg .arara {
  animation: grow-svg 2s infinite alternate ease-in-out;
}
svg .caneta {
  animation: grow-svg-caneta 2s infinite alternate ease-in-out 2s;
}
@keyframes grow-svg {
  0% {
    scale: 1;
  }
  100% {
    scale: 0.8;
  }
}
@keyframes grow-svg-caneta {
  0% {
    scale: 1;
  }
  100% {
    scale: 0.8;
    transform: translateY(20px);
  }
}
.loading-holder {
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.loading-logo {
  display: flex;
  justify-content: center;
  align-items: center;
}
.loading {
  display: flex;
  justify-content: center;
  align-items: center;
  .ball {
    width: 10px;
    height: 10px;
    background-color: var(--blue);
    border-radius: 50%;
    margin: 0 5px;
    animation: grow 0.8s infinite alternate;
    margin-top: 50px;
    @for $i from 1 through 5 {
      &:nth-child(#{$i}) {
        animation-delay: 0.3s * $i;
      }
    }
  }
}

@keyframes grow {
  0% {
    scale: 1;
    transform: translateY(0);
  }
  100% {
    scale: 1.3;
    transform: translateY(-20px);
  }
}
</style>
